mapper.update:
update book
	set bookcode=#{bookcode}, pdf_name=#{pdf_name}, img_name=#{img_name}, book_name=#{book_name}, author=#{author}, translator=#{translator}, publication_date=#{publication_date}, publisher=#{publisher}, pages=#{pages}, isbn=#{isbn}, location=#{location}, memo=#{memo}, copies=#{copies}
	where id=#{id}
